    private void AddButtonActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_AddButtonActionPerformed
    {//GEN-HEADEREND:event_AddButtonActionPerformed
        String type = "";
        if(typeComboBox.getSelectedItem().toString()== "Custom")
        {
            type = typeTextBox.getText();
        }
        else
        {
            type = typeComboBox.getSelectedItem().toString();
        }
        GUIMain._instance.AddVar(type, VarNameText.getText(), ArrayCheck.isSelected(), ArrayListCheck.isSelected(), GetterCheck.isSelected(), SetterCheck.isSelected(), RetrievableCheck.isSelected());
        
        this.dispose();
    }//GEN-LAST:event_AddButtonActionPerformed

    private void typeComboBoxActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_typeComboBoxActionPerformed
    {//GEN-HEADEREND:event_typeComboBoxActionPerformed
        if(typeComboBox.getSelectedItem() == "Custom")
        {
            typeTextBox.setEnabled(true);
        }
        else
        {
            typeTextBox.setEnabled(false);
        }
    }//GEN-LAST:event_typeComboBoxActionPerformed

