    public void preSet(String VarName, String Type, boolean Array, boolean ArrayList, boolean get, boolean set, boolean retrieveable)
    {
        VarNameText.setText(VarName);
        typeComboBox.setSelectedItem(Type);
        ArrayCheck.setSelected(Array);
        ArrayListCheck.setSelected(ArrayList);
        GetterCheck.setSelected(get);
        SetterCheck.setSelected(set);
        RetrievableCheck.setSelected(retrieveable);
    }
    public void preSet(String VarName, String Type, String Custom, boolean Array, boolean ArrayList, boolean get, boolean set, boolean retrieveable)
    {
        VarNameText.setText(VarName);
        typeComboBox.setSelectedItem("Custom");
        typeTextBox.setText(Custom);
        ArrayCheck.setSelected(Array);
        ArrayListCheck.setSelected(ArrayList);
        GetterCheck.setSelected(get);
        SetterCheck.setSelected(set);
        RetrievableCheck.setSelected(retrieveable);
    }
        
    
